import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { TextField, Button } from '@mui/material';

function MapComponent() {
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [map, setMap] = useState(null);
  const [searchControl, setSearchControl] = useState(null);

  useEffect(() => {
    ymaps.ready(() => {
      const myMap = new ymaps.Map('map', {
        center: [55.751426, 37.618879],
        zoom: 13,
        controls: []
      });
      setMap(myMap);

      const searchControl = new ymaps.control.SearchControl({
        options: {
          provider: 'yandex#search'
        }
      });
      myMap.controls.add(searchControl);
      setSearchControl(searchControl);
    });
  }, []);

  const handleSearch = () => {
    if (searchControl) {
      searchControl.search(searchQuery);
      searchControl.events.add('resultselect', (e) => {
        const index = e.get('index');
        searchControl.getResult(index).then((result) => {
          const place = {
            place_id: result.properties.get('id'),
            display_name: result.properties.get('name'),
            lat: result.geometry.getCoordinates()[0],
            lon: result.geometry.getCoordinates()[1],
            address: result.properties.get('description'),
            category: 'unknown',
            type: 'unknown',
            tags: {}
          };
          setSearchResults([...searchResults, place]);
        });
      });
    }
  };

  const handleSavePlace = (place) => {
    axios.post('http://localhost:5000/api/user-data', place)
      .then(response => {
        console.log('Place saved successfully:', response.data);
      })
      .catch(error => {
        console.error('Error saving place:', error);
      });
  };

  return (
    <div>
      <TextField
        label="Search for a place"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <Button variant="contained" onClick={handleSearch}>Search</Button>

      <div id="map" style={{ width: '100%', height: '500px' }}></div>

      {searchResults.map(result => (
        <div key={result.place_id}>
          <b>{result.display_name}</b>
          <p>{result.address}</p>
          <p>{result.lat}</p>
          <p>{result.lon}</p>
          <Button variant="contained" onClick={() => handleSavePlace(result)}>Save</Button>
        </div>
      ))}
    </div>
  );
}

export default MapComponent;
