import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import {
  Typography,
  Container,
  Paper,
  CircularProgress,
  Button,
  TextField,
  Grid,
} from '@mui/material';
import axios from 'axios';

const PlaceInfo = () => {
  const { id } = useParams(); // Get the place ID from the URL
  const [place, setPlace] = useState(null);
  const [loading, setLoading] = useState(true);
  const [editMode, setEditMode] = useState(false); // Toggle edit mode
  const [formData, setFormData] = useState({
    name: '',
    address: '',
    category: [],
    description: '',
    work_hours: '',
    website: '',
    phone: '',
  });

  // Fetch place details from the backend
  useEffect(() => {
    const fetchPlace = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/place/${id}`);
        setPlace(response.data.place);
        setFormData({
          name: response.data.place.name,
          address: response.data.place.address,
          category: response.data.place.category.join(', '), // Convert array to string
          description: response.data.place.description,
          work_hours: response.data.place.work_hours,
          website: response.data.place.website,
          phone: response.data.place.phone,
        });
        setLoading(false);
      } catch (error) {
        console.error('Error fetching place details:', error);
        setLoading(false);
      }
    };

    fetchPlace();
  }, [id]);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.put(`http://localhost:5000/api/place/${id}`, {
        name: formData.name,
        address: formData.address,
        category: formData.category.split(',').map((item) => item.trim()), // Convert string back to array
        description: formData.description,
        work_hours: formData.work_hours,
        website: formData.website,
        phone: formData.phone,
      });
      setPlace(response.data.place); // Update place details
      setEditMode(false); // Exit edit mode
    } catch (error) {
      console.error('Error updating place details:', error);
    }
  };

  return (
    <Container maxWidth="md" sx={{ marginTop: 4 }}>
      <Paper elevation={3} sx={{ padding: 3, borderRadius: 2 }}>
        {loading ? (
          <CircularProgress />
        ) : place ? (
          <>
            <Typography variant="h4" gutterBottom>
              {editMode ? 'Edit Place' : place.name}
            </Typography>
            {editMode ? (
              <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Name"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Address"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Category (comma-separated)"
                      name="category"
                      value={formData.category}
                      onChange={handleInputChange}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Description"
                      name="description"
                      value={formData.description}
                      onChange={handleInputChange}
                      multiline
                      rows={4}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Work Hours"
                      name="work_hours"
                      value={formData.work_hours}
                      onChange={handleInputChange}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Website"
                      name="website"
                      value={formData.website}
                      onChange={handleInputChange}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      fullWidth
                      label="Phone"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Button type="submit" variant="contained" color="primary">
                      Save
                    </Button>
                    <Button
                      variant="outlined"
                      color="secondary"
                      onClick={() => setEditMode(false)}
                      sx={{ marginLeft: 2 }}
                    >
                      Cancel
                    </Button>
                  </Grid>
                </Grid>
              </form>
            ) : (
              <>
                <Typography variant="subtitle1" gutterBottom>
                  Address: {place.address}
                </Typography>
                <Typography variant="body1" gutterBottom>
                  Category: {place.category.join(', ')}
                </Typography>
                <Typography variant="body1" gutterBottom>
                  Description: {place.description}
                </Typography>
                <Typography variant="body1" gutterBottom>
                  Work Hours: {place.work_hours}
                </Typography>
                <Typography variant="body1" gutterBottom>
                  Website: <a href={place.website} target="_blank" rel="noopener noreferrer">{place.website}</a>
                </Typography>
                <Typography variant="body1" gutterBottom>
                  Phone: {place.phone}
                </Typography>
                <Button
                  variant="contained"
                  color="primary"
                  onClick={() => setEditMode(true)}
                  sx={{ marginTop: 2 }}
                >
                  Edit
                </Button>
              </>
            )}
          </>
        ) : (
          <Typography variant="h6" color="error">
            Place not found.
          </Typography>
        )}
      </Paper>
    </Container>
  );
};

export default PlaceInfo;
