import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button } from '@mui/material';

function MapComponent() {
  const [selectedPlace, setSelectedPlace] = useState(null);
  const [map, setMap] = useState(null);
  const [searchControl, setSearchControl] = useState(null);

  useEffect(() => {
    ymaps.ready(() => {
      console.log('Yandex Maps API is ready');
      const myMap = new ymaps.Map('map', {
        center: [55.751426, 37.618879],
        zoom: 13,
        controls: []
      });
      setMap(myMap);
      console.log('Map initialized');

      const searchControl = new ymaps.control.SearchControl({
        options: {
          provider: 'yandex#search',
          noSuggestPanel: true
        }
      });
      myMap.controls.add(searchControl);
      setSearchControl(searchControl);
      console.log('Search control added');

      // Add event listener for search result selection
      searchControl.events.add('resultselect', (e) => {
        console.log('Search result selected');
        const index = e.get('index');
        searchControl.getResult(index).then((result) => {
          console.log('Search result details:', result);
          const place = {
            place_id: result.properties.get('id'),
            display_name: result.properties.get('name'),
            lat: result.geometry.getCoordinates()[1],
            lon: result.geometry.getCoordinates()[0],
            address: result.properties.get('description'),
            categories: result.properties.get('Categories', []).map(category => category.name),
            work_hours: result.properties.get('Hours', {}).text,
            phone: result.properties.get('Phones', []).map(phone => phone.formatted),
            url: result.properties.get('url')
          };
          setSelectedPlace(place);
          console.log('Selected place:', place);
        });
      });
    });
  }, []);

  const handleSavePlace = (place) => {
    axios.post('http://localhost:5000/api/place-data', place)
      .then(response => {
        console.log('Place saved successfully:', response.data);
      })
      .catch(error => {
        console.error('Error saving place:', error);
      });
  };

  return (
    <div>
      <div id="map" style={{ width: '100%', height: '500px' }}></div>

      {selectedPlace && (
        <div>
          <b>{selectedPlace.display_name}</b>
          <p>{selectedPlace.address}</p>
          <p>{selectedPlace.lat}</p>
          <p>{selectedPlace.lon}</p>
          <p>Categories: {selectedPlace.categories.join(', ')}</p>
          <p>Work Hours: {selectedPlace.work_hours}</p>
          <p>Phone: {selectedPlace.phone.join(', ')}</p>
          <Button variant="contained" onClick={() => handleSavePlace(selectedPlace)}>Save</Button>
        </div>
      )}
    </div>
  );
}

export default MapComponent;
