import React, { useState } from 'react';
import axios from 'axios';
import { TextField, Button, Typography, Box } from '@mui/material';

function Register() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
const backendUrl = "https://places-project-6i0r.onrender.com";

  const handleRegister = async () => {
    try {
      const response = await axios.post(`${backendUrl}/api/register`, {
        username,
        password,
      });
      setMessage(response.data.message); // Display success message
    } catch (error) {
      // Check if error.response exists
      if (error.response) {
        setMessage(error.response.data.message || 'Registration failed');
      } else {
        setMessage('Network error or server not responding'); // Generic error message
      }
    }
  };

  return (
    <Box
      display="flex"
      flexDirection="column"
      alignItems="center"
      justifyContent="center"
      minHeight="100vh"
    >
      <Typography variant="h4" gutterBottom>
        Register
      </Typography>
      <TextField
        label="Username"
        variant="outlined"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        margin="normal"
      />
      <TextField
        label="Password"
        variant="outlined"
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        margin="normal"
      />
      <Button variant="contained" color="primary" onClick={handleRegister}>
        Register
      </Button>
      <Typography variant="body1" color="error" marginTop={2}>
        {message}
      </Typography>
    </Box>
  );
}

export default Register;
