import React, { useEffect, useState } from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import axios from 'axios';
import { TextField, Button } from '@mui/material';

function MapComponent() {
  const [places, setPlaces] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/places')
      .then(response => {
        setPlaces(response.data);
      })
      .catch(error => {
        console.error('Error fetching places:', error);
      });
  }, []);

  const handleSearch = () => {
    axios.get(`http://localhost:5000/api/search?q=${searchQuery}`)
      .then(response => {
        console.log('Search results:', response.data); // Debugging statement
        setSearchResults(response.data);
      })
      .catch(error => {
        console.error('Error searching places:', error);
      });
  };

  const handleSavePlace = (place) => {
    axios.post('http://localhost:5000/api/places', place)
      .then(response => {
        console.log('Place saved successfully:', response.data);
        setPlaces([...places, place]);
      })
      .catch(error => {
        console.error('Error saving place:', error);
      });
  };

  return (
    <div>
      <TextField
        label="Search for a place"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <Button variant="contained" onClick={handleSearch}>Search</Button>

      <MapContainer center={[55.751426, 37.618879]} zoom={13} style={{ height: '500px' }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        {places.map(place => (
          <Marker
            key={place.place_id}
            position={[place.latitude, place.longitude]}
          >
            <Popup>
              <b>{place.name}</b>
              <p>{place.address}</p>
            </Popup>
          </Marker>
        ))}
        {searchResults.map(result => (
          <Marker
            key={result.place_id}
            position={[parseFloat(result.lat), parseFloat(result.lon)]}
          >
            <Popup>
              <b>{result.display_name}</b>
              <p>{result.address}</p>
              <p>{result.lat}</p>
              <p>{result.lon}</p>
              <Button variant="contained" onClick={() => handleSavePlace({
                place_id: result.place_id,
                name: result.display_name,
                latitude: parseFloat(result.lat),
                longitude: parseFloat(result.lon),
                address: result.address,
                category: result.category || 'unknown',
                type: result.type || 'unknown',
                tags: result.tags || {}
              })}>Save</Button>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

export default MapComponent;
