import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Typography, Container, Paper, CircularProgress } from '@mui/material';
import axios from 'axios';

const PlaceInfo = () => {
  const { id } = useParams(); // Get the place ID from the URL
  const [place, setPlace] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch place details from the backend
  useEffect(() => {
    const fetchPlace = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/place/${id}`);
        setPlace(response.data.place);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching place details:', error);
        setLoading(false);
      }
    };

    fetchPlace();
  }, [id]);

  return (
    <Container maxWidth="md" sx={{ marginTop: 4 }}>
      <Paper elevation={3} sx={{ padding: 3, borderRadius: 2 }}>
        {loading ? (
          <CircularProgress />
        ) : place ? (
          <>
            <Typography variant="h4" gutterBottom>
              {place.name}
            </Typography>
            <Typography variant="subtitle1" gutterBottom>
              Address: {place.address}
            </Typography>
            <Typography variant="body1" gutterBottom>
              Category: {place.category.join(', ')}
            </Typography>
            <Typography variant="body1" gutterBottom>
              Description: {place.description}
            </Typography>
            <Typography variant="body1" gutterBottom>
              Work Hours: {place.work_hours}
            </Typography>
            <Typography variant="body1" gutterBottom>
              Website: <a href={place.website} target="_blank" rel="noopener noreferrer">{place.website}</a>
            </Typography>
            <Typography variant="body1" gutterBottom>
              Phone: {place.phone}
            </Typography>
          </>
        ) : (
          <Typography variant="h6" color="error">
            Place not found.
          </Typography>
        )}
      </Paper>
    </Container>
  );
};

export default PlaceInfo;
