import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { TextField, Button } from '@mui/material';

function MapComponent() {
  const [places, setPlaces] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [map, setMap] = useState(null);
  const [searchControl, setSearchControl] = useState(null);

  useEffect(() => {
    ymaps.ready(() => {
      const myMap = new ymaps.Map('map', {
        center: [55.751426, 37.618879],
        zoom: 13,
        controls: []
      });
      setMap(myMap);

      const searchControl = new ymaps.control.SearchControl({
        options: {
          provider: 'yandex#search'
        }
      });
      myMap.controls.add(searchControl);
      setSearchControl(searchControl);
    });

    axios.get('http://localhost:5000/api/places')
      .then(response => {
        setPlaces(response.data);
      })
      .catch(error => {
        console.error('Error fetching places:', error);
      });
  }, []);

  const handleSearch = () => {
    if (searchControl) {
      searchControl.search(searchQuery);
    }
  };

  const handleSavePlace = (place) => {
    axios.post('http://localhost:5000/api/places', place)
      .then(response => {
        console.log('Place saved successfully:', response.data);
        setPlaces([...places, place]);
      })
      .catch(error => {
        console.error('Error saving place:', error);
      });
  };

  return (
    <div>
      <TextField
        label="Search for a place"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
      />
      <Button variant="contained" onClick={handleSearch}>Search</Button>

      <div id="map" style={{ width: '100%', height: '500px' }}></div>

      {searchResults.map(result => (
        <div key={result.place_id}>
          <b>{result.display_name}</b>
          <p>{result.address}</p>
          <p>{result.lat}</p>
          <p>{result.lon}</p>
          <Button variant="contained" onClick={() => handleSavePlace({
            place_id: result.place_id,
            name: result.display_name,
            latitude: result.lat,
            longitude: result.lon,
            address: result.address,
            category: result.category || 'unknown',
            type: result.type || 'unknown',
            tags: result.tags || {}
          })}>Save</Button>
        </div>
      ))}
    </div>
  );
}

export default MapComponent;
