import React, { useState, useEffect } from 'react';
import { Typography, Container, Grid, Paper, List, ListItem, ListItemText, Divider, CircularProgress } from '@mui/material';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Homepage = () => {
  const [places, setPlaces] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch places from the backend
  useEffect(() => {
    const fetchPlaces = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/places');
        setPlaces(response.data.places);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching places:', error);
        setLoading(false);
      }
    };

    fetchPlaces();
  }, []);

  return (
    <Container maxWidth="lg" sx={{ marginTop: 4 }}>
      <Grid container spacing={4}>
        {/* Section 1: Places */}
        <Grid item xs={12} md={6} lg={3}>
          <Paper elevation={3} sx={{ padding: 3, textAlign: 'center', borderRadius: 2, height: 400, overflowY: 'auto' }}>
            <Typography variant="h5" gutterBottom>
              Places
            </Typography>
            {loading ? (
              <CircularProgress />
            ) : (
              <List>
                {places.slice(0, 5).map((place) => (
                  <React.Fragment key={place.id}>
                    <ListItem
                      component={Link}
                      to={`/place/${place.id}`}
                      sx={{ textDecoration: 'none', color: 'inherit' }}
                    >
                      <ListItemText primary={place.name} secondary={place.address} />
                    </ListItem>
                    <Divider />
                  </React.Fragment>
                ))}
              </List>
            )}
          </Paper>
        </Grid>

        {/* Other sections (Lists, Reviews, Events) */}
        {/* ... */}
      </Grid>
    </Container>
  );
};

export default Homepage;
